// Parmetros necesarios para gestionar el envio de datos de las figuras por la USART
int i_data = 0;
int i_pkt = 0;
int i_fig = 0;
int max_fig_print = 5;
int times_printed = 0;

/**
 * Mtodo que aade un delay
 */
void inline __attribute__((always_inline)) delay(uint32_t delay)
{
   while(delay--) __asm("");
}

int main(void){
	// Inicializaciones
	setNInt(0);
	setTRight(0);
	setTLeft(0);
	setMode(-1);
	setTOn(10000);
	setDutyCycle(50);

	InitializeLED();
	InitializeUserButton();
	InitializeTestPorts();
	InitializePWM3();
	InitializePWM10();
	InitializePWM11();
	EnableInterrupts();
	EnableTimerInterrupt();
	InitializeTMR2();
	InitializeTMR5();
	InitializeTMR3();
	InitializeTMR10();
	InitializeTMR11();
	InitializeDMA1();
	InitializeDMA_MemToMem();
	InitializeUSART1();
	InitializeUSART2();
	InitializeLCD();
	GPIO_ToggleBits(GPIOE, GPIO_Pin_10);
	printAxis();

	//Infinite loop
	while (1){

		if(GPIO_ReadInputDataBit(GPIOA, GPIO_Pin_0)){
    		GPIO_ToggleBits(GPIOD, GPIO_Pin_12);
			setMode(getMode() + 1);

			if(getMode() == 2){
				setMode(0);
			}
			//Primera pulsacio (start)

			TIM_Cmd(TIM10, ENABLE);
            TIM_Cmd(TIM11, ENABLE);

			//Segona pulsacio (stop)
			if(getMode() == 1){
				setTOn(10000);
				setDutyCycle(50);
				InitializeTMR3();
				TIM_Cmd(TIM10, DISABLE);
                TIM_Cmd(TIM11, DISABLE);
			}
		}

		//Esperem a l'estat de repos del pulsador
		while (GPIO_ReadInputDataBit(GPIOA, GPIO_Pin_0)){}

		// Envia los datos por la USART, simula el envio de datos del sensor LIDAR. Envia la misma figura 4 veces para que se mantenga un tiempo en la pantalla.
		// Se puede modificar este vlaor en la variable "max_fig_print" (global).
		USART_SendData(USART1, figures[i_fig][i_pkt][i_data]);

		delay(5000);
		i_data++;
		if(i_data == MAX_TRAMA){
			i_data = 0;
			i_pkt++;
			if(i_pkt == 4){
				i_pkt = 0;
				times_printed++;
				if(max_fig_print == times_printed){
					times_printed = 0;
					i_fig++;

					// Resetea la pantalla para poder printar la nueva figura.
					EsborraPantalla(255, 255, 255);
					printAxis();
					if(i_fig == 4){
						i_fig = 0;
					}
				}
			}
		}

	}
}

